mapper.select:
SELECT BTI.CONTENTID, BTI.TITLE, BTI.FIRSTIMAGE, BTI.SIGUNGUCODE, BTI.AREACODE, AC.DETAILAREANAME, AC.AREANAME, BTI.OVERVIEW, NVL(STRPT.CNTREVSTAR,0) AS CNTREVSTAR FROM BASIC_TOUR_INFO BTI left outer JOIN AREACODE AC ON (AC.AREACODE = BTI.AREACODE AND AC.SIGUNGUCODE = BTI.SIGUNGUCODE) left outer JOIN (SELECT CEIL(AVG(STAR)) AS CNTREVSTAR, CONTENTID FROM REVIEW_TOTAL GROUP BY CONTENTID) STRPT ON (BTI.CONTENTID = STRPT.CONTENTID) WHERE 1=1 AND CONTENTTYPEID = 25
AND AC.AREACODE = #{areaCode}
AND AC.SIGUNGUCODE = #{sigunguCode}
ORDER BY CNTREVSTAR DESC

SELECT COUNT(*) FROM (SELECT BTI.CONTENTID, BTI.TITLE, BTI.FIRSTIMAGE, BTI.SIGUNGUCODE, BTI.AREACODE, AC.DETAILAREANAME, AC.AREANAME, BTI.OVERVIEW, NVL(STRPT.CNTREVSTAR,0) AS CNTREVSTAR FROM BASIC_TOUR_INFO BTI left outer JOIN AREACODE AC ON (AC.AREACODE = BTI.AREACODE AND AC.SIGUNGUCODE = BTI.SIGUNGUCODE) left outer JOIN (SELECT CEIL(AVG(STAR)) AS CNTREVSTAR, CONTENTID FROM REVIEW_TOTAL GROUP BY CONTENTID) STRPT ON (BTI.CONTENTID = STRPT.CONTENTID) WHERE 1=1 AND CONTENTTYPEID = 25
AND AC.AREACODE = #{areaCode}
AND AC.SIGUNGUCODE = #{sigunguCode}
ORDER BY CNTREVSTAR DESC)
